<?phpClass FakeNotificationAdmin {    static function register($tabs) {        $tabs['fake_notification'] = [            'label'         => 'Thông báo ảo',            'description'   => 'Quản lý thông báo đơn hàng ảo',            'callback'      => 'FakeNotificationAdmin::render',            'icon'          => '<i class="fad fa-bell"></i>',        ];        return $tabs;    }    static function render(): void {        $productPlugin      = class_exists('sicommerce');        $Form               = new FormBuilder();        $productsCategories = ($productPlugin) ? ProductCategory::gets(Qr::set()->categoryType('options')) : [];        $dataType           = Fake_Notification::config('dataType');        Plugin::partial(FAKE_NOTIFICATION_NAME, 'admin/views/config', [            'productsCategories'    => $productsCategories,            'Form'                  => $Form,            'dataType'              => $dataType,            'productPlugin'         => $productPlugin        ]);    }}add_filter('skd_system_tab', 'FakeNotificationAdmin::register', 50);